package p1513;

import org.junit.Assert;

class Solution {

    private static final int MOD = 1_000_000_007;
    public int numSub(String s) {
        int start = -1;
        long ans = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '0') {
                if (start != -1) {
                    ans = (ans  + count(i - start)) % MOD;
                    start = -1;
                }
            } else if (start == -1) {
                start = i;
            }
        }
        if (start != -1) {
            ans = (ans + count(s.length() - start)) % MOD;
        }
        return (int) ans;
    }

    private long count(int n) {
        return (n * (n + 1) % MOD) / 2;
    }

    public static void main(String[] args) {
        Solution s = new Solution();
//        Assert.assertEquals(200542505, s.numSub(
//                ""));
        Assert.assertEquals(9, s.numSub("0110111"));
        Assert.assertEquals(2, s.numSub("101"));
        Assert.assertEquals(0, s.numSub("000"));
        Assert.assertEquals(21, s.numSub("111111"));
    }
}
